import React, { useEffect, useState } from "react";
import { Pie, Bar } from "react-chartjs-2";
import {
  Chart as ChartJS,
  ArcElement,
  Tooltip,
  Legend,
  BarElement,
  CategoryScale,
  LinearScale,
} from "chart.js";

// Chart.js에 필요한 요소 등록
ChartJS.register(
  ArcElement,
  Tooltip,
  Legend,
  BarElement,
  CategoryScale,
  LinearScale
);

function Graph_5() {
  const [mainData, setMainData] = useState(null);
  const [detailData, setDetailData] = useState(null);
  const [selectedReason, setSelectedReason] = useState(null);
  const [detailChartData, setDetailChartData] = useState(null);

  useEffect(() => {
    // 메인 데이터 가져오기
    fetch("/status-data2")
      .then((response) => response.json())
      .then((data) => {
        setMainData({
          labels: ["정상", "불량"],
          datasets: [
            {
              data: [data.정상, data.불량],
              backgroundColor: ["#36A2EB", "#FF6384"],
              hoverBackgroundColor: ["#36A2EB", "#FF6384"],
            },
          ],
        });
      })
      .catch((error) => console.error("Error fetching data:", error));
  }, []);

  const handlePieClick = (event, elements) => {
    if (elements.length > 0) {
      const index = elements[0].index;
      const label = mainData.labels[index];

      if (label === "불량") {
        fetch("/fault-details")
          .then((response) => response.json())
          .then((data) => {
            setDetailData({
              labels: data.map((item) => item["사유코드설명"]),
              datasets: [
                {
                  data: data.map((item) => item.count),
                  backgroundColor: [
                    "#FF9F40",
                    "#FF6384",
                    "#36A2EB",
                    "#4BC0C0",
                    "#4BC0FF",
                  ],
                },
              ],
            });
            setSelectedReason(label);
          })
          .catch((error) =>
            console.error("Error fetching fault details:", error)
          );
      }
    }
  };

  const handleDetailPieClick = (event, elements) => {
    if (elements.length > 0) {
      const index = elements[0].index;
      const reasonCode = detailData.labels[index];

      fetch(`/detail-data/${reasonCode}`)
        .then((response) => response.json())
        .then((data) => {
          setDetailChartData({
            labels: data.map((item) => item["선종"]),
            datasets: [
              {
                data: data.map((item) => item.count),
                backgroundColor: ["#FF6384", "#36A2EB", "#FFCE56"],
              },
            ],
          });
        })
        .catch((error) => console.error("Error fetching detail data:", error));
    }
  };

  return (
    <div>
      <h1>사유코드 설명 파이 차트</h1>

      {mainData && (
        <Pie
          data={mainData}
          options={{
            responsive: true,
            onClick: (event, elements) => handlePieClick(event, elements),
          }}
        />
      )}

      {detailData && selectedReason === "불량" && (
        <Pie
          data={detailData}
          options={{
            responsive: true,
            onClick: (event, elements) => handleDetailPieClick(event, elements),
          }}
        />
      )}

      {detailChartData && (
        <Bar
          data={detailChartData}
          options={{
            responsive: true,
          }}
        />
      )}
    </div>
  );
}

export default Graph_5;
