import { useNavigate } from "react-router-dom";

const Menu = () => {
  const [hoveredIndex, setHoveredIndex] = useState(null);
  const navigate = useNavigate(); // 라우팅을 위한 navigate 함수

  // 메뉴 항목 정의
  const menuItems = ["Home", "About", "Services", "Contact"];

  const handleMenuClick = (item) => {
    // 각 메뉴 항목에 따라 URL 변경
    let path = "";
    switch (item) {
      case "Home":
        path = "/menu1";
        break;
      case "About":
        path = "/menu2";
        break;
      case "Services":
        path = "/menu3";
        break;
      case "Contact":
        path = "/menu4";
        break;
      default:
        path = "/";
    }
    navigate(path); // 해당 경로로 이동
  };

  return (
    <div className="menu_container">
      <div className="logo">
        <img src="/path/to/logo.jpg" alt="Logo" />
      </div>
      <ul>
        {menuItems.map((item, index) => (
          <li
            key={index}
            onMouseEnter={() => setHoveredIndex(index)}
            onMouseLeave={() => setHoveredIndex(null)}
            onClick={() => handleMenuClick(item)} // 메뉴 클릭 시 handleMenuClick 호출
            style={{
              cursor: "pointer",
              backgroundColor: hoveredIndex === index ? "white" : "#013E8F",
              color: hoveredIndex === index ? "#333" : "white",
              transition: "background-color 0.3s ease, color 0.3s ease",
            }}
          >
            {item}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Menu;
