import React, { useEffect, useRef } from "react";
import Chart from "chart.js/auto";
import "./Notice.css";

const Notice = () => {
  const chartRef = useRef(null); // 차트 렌더링을 위한 ref

  useEffect(() => {
    const fetchDataAndDrawChart = async () => {
      try {
        // 서버에서 데이터 가져오기
        const response = await fetch("/n1-p1");
        const data = await response.json();

        // 파이 차트를 그릴 컨텍스트
        const ctx = chartRef.current.getContext("2d");

        // 어두운 파랑 계열 색상 설정
        const darkBlueColors = [
          "#0B3D91", // 어두운 파랑
          "#0D47A1", // 약간 밝은 파랑
          "#1565C0", // 더 밝은 파랑
          "#1976D2", // 밝은 파랑
          "#1E88E5", // 가장 밝은 파랑
        ];

        // 파이 차트 데이터 설정
        const chartData = {
          labels: ["근원부서 없음", "근원부서 존재"],
          datasets: [
            {
              data: [data.근원부서_존재하지않음, data.근원부서_존재], // 서버에서 받은 데이터
              backgroundColor: darkBlueColors.slice(0, 2), // 파란색 계열 색상
              hoverBackgroundColor: darkBlueColors.slice(0, 2),
            },
          ],
        };

        // 파이 차트 생성
        new Chart(ctx, {
          type: "pie",
          data: chartData,
          options: {
            responsive: true,
            plugins: {
              title: {
                display: true,
                text: "P1 개정도 현황", // 그래프 상단의 제목
                font: {
                  size: 18,
                },
              },
              legend: {
                position: "bottom", // 레이블을 그래프 아래에 표시
              },
            },
          },
        });
      } catch (error) {
        console.error("데이터를 가져오는 중 오류 발생: ", error);
      }
    };

    fetchDataAndDrawChart(); // 데이터 가져와서 차트 그리기
  }, []);

  return (
    <div className="notice">
      <h2>공정현황 그래프</h2>
      <canvas
        ref={chartRef}
        style={{ width: "50%", height: "50%" }}
      ></canvas>{" "}
      {/* 차트가 그려질 캔버스 */}
    </div>
  );
};

export default Notice;
