import Chart from "chart.js/auto";

const loadChartJs = () => {
  return new Promise((resolve) => {
    const script = document.createElement("script");
    script.src = "https://cdn.jsdelivr.net/npm/chart.js";
    script.onload = () => resolve();
    document.head.appendChild(script);
  });
};

const Graph5 = () => {
  return new Promise((resolve) => {
    loadChartJs().then(() => {
      let myPieChart;
      let detailPieChart;
      let detailChart;

      document.addEventListener("DOMContentLoaded", () => {
        fetch("/status-data2")
          .then((response) => response.json())
          .then((data) => {
            const ctx = document.getElementById("myPieChart").getContext("2d");

            const chartData = {
              labels: ["정상", "불량"],
              datasets: [
                {
                  data: [data.정상, data.불량],
                  backgroundColor: ["#36A2EB", "#FF6384"],
                  hoverBackgroundColor: ["#36A2EB", "#FF6384"],
                },
              ],
            };

            myPieChart = new Chart(ctx, {
              type: "pie",
              data: chartData,
              options: {
                responsive: false,
                onClick: (evt, activeElements) => {
                  if (activeElements.length > 0) {
                    const index = activeElements[0].index;
                    const label = myPieChart.data.labels[index];

                    if (label === "불량") {
                      fetch("/fault-details")
                        .then((response) => response.json())
                        .then((detailData) => {
                          const detailCtx = document
                            .getElementById("detailPieChart")
                            .getContext("2d");
                          const labels = detailData.map(
                            (item) => item["사유코드설명"]
                          );
                          const counts = detailData.map((item) => item.count);

                          if (detailPieChart) detailPieChart.destroy();

                          detailPieChart = new Chart(detailCtx, {
                            type: "pie",
                            data: {
                              labels: labels,
                              datasets: [
                                {
                                  data: counts,
                                  backgroundColor: [
                                    "#FF9F40",
                                    "#FF6384",
                                    "#36A2EB",
                                    "#4BC0C0",
                                    "#4BC0FF",
                                  ],
                                },
                              ],
                            },
                            options: {
                              responsive: false,
                              onClick: (evt, activeElements) => {
                                if (activeElements.length > 0) {
                                  const index = activeElements[0].index;
                                  const clickedLabel =
                                    detailPieChart.data.labels[index];
                                  loadDetailChart(clickedLabel);
                                }
                              },
                            },
                          });

                          document.getElementById(
                            "detailPieChart"
                          ).style.display = "block";
                        });
                    }
                  }
                },
              },
            });
          });

        function loadDetailChart(reasonCode) {
          fetch(`/detail-data/${reasonCode}`)
            .then((response) => response.json())
            .then((detailData) => {
              const detailCtx = document
                .getElementById("detailChart")
                .getContext("2d");
              const detailLabels = detailData.map((item) => item["선종"]);
              const detailCounts = detailData.map((item) => item.count);

              if (detailChart) {
                detailChart.destroy();
              }

              detailChart = new Chart(detailCtx, {
                type: "bar",
                data: {
                  labels: detailLabels,
                  datasets: [
                    {
                      data: detailCounts,
                      backgroundColor: ["#FF6384", "#36A2EB", "#FFCE56"],
                    },
                  ],
                },
                options: {
                  responsive: false,
                },
              });

              document.getElementById("detailChart").style.display = "block";
            });
        }

        // HTML 요소 생성 및 스타일 적용
        const container = document.createElement("div");
        document.body.appendChild(container);

        const h1 = document.createElement("h1");
        h1.textContent = "사유코드 설명 파이 차트";
        container.appendChild(h1);

        const myPieChartCanvas = document.createElement("canvas");
        myPieChartCanvas.id = "myPieChart";
        myPieChartCanvas.style.width = "50%";
        myPieChartCanvas.style.height = "50%";
        container.appendChild(myPieChartCanvas);

        const detailPieChartCanvas = document.createElement("canvas");
        detailPieChartCanvas.id = "detailPieChart";
        detailPieChartCanvas.style.display = "none";
        detailPieChartCanvas.style.width = "50%";
        detailPieChartCanvas.style.height = "50%";
        container.appendChild(detailPieChartCanvas);

        const detailChartCanvas = document.createElement("canvas");
        detailChartCanvas.id = "detailChart";
        detailChartCanvas.style.display = "none";
        detailChartCanvas.style.width = "50%";
        detailChartCanvas.style.height = "50%";
        container.appendChild(detailChartCanvas);

        resolve(); // Promise 완료
      });
    });
  });
};

export default Graph5;
