import React, { useState } from "react";
import "./Menu.css";
import { useNavigate } from "react-router-dom";

const Menu = () => {
  const [hoveredIndex, setHoveredIndex] = useState(null);
  const [selectedIndex, setSelectedIndex] = useState(null); // 선택된 메뉴 인덱스 상태
  const navigate = useNavigate();

  const menuItems = ["Home", "About", "Services", "Contact"];

  const handleMenuClick = (item, index) => {
    setSelectedIndex(index); // 선택된 메뉴 인덱스 업데이트
    let path = "";
    switch (item) {
      case "Home":
        path = "/menu1";
        break;
      case "About":
        path = "/menu2";
        break;
      case "Services":
        path = "/menu3";
        break;
      case "Contact":
        path = "/menu4";
        break;
      default:
        path = "/";
    }
    navigate(path);
  };

  return (
    <div className="menu_container">
      <div className="logo">
        <img src="/path/to/logo.jpg" alt="Logo" />
      </div>
      <ul>
        {menuItems.map((item, index) => (
          <li
            key={index}
            onMouseEnter={() => setHoveredIndex(index)}
            onMouseLeave={() => setHoveredIndex(null)}
            onClick={() => handleMenuClick(item, index)}
            style={{
              cursor: "pointer",
              backgroundColor: selectedIndex === index ? "white" : "#013E8F",
              color: selectedIndex === index ? "#333" : "white",
              transition: "background-color 0.3s ease, color 0.3s ease",
            }}
          >
            {item}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Menu;
