import React, { useEffect, useRef } from "react";
import Chart from "chart.js/auto";
import ChartDataLabels from "chartjs-plugin-datalabels"; // 데이터 라벨 플러그인
import "./Notice.css";

const Notice = () => {
  const chartRef = useRef(null); // 차트 렌더링을 위한 ref

  useEffect(() => {
    const fetchDataAndDrawChart = async () => {
      try {
        // 서버에서 데이터 가져오기
        const response = await fetch("/n1-p1");
        const data = await response.json();

        // 파이 차트를 그릴 컨텍스트
        const ctx = chartRef.current.getContext("2d");

        // 파이 차트 데이터 설정
        const chartData = {
          labels: ["근원부서 없음", "근원부서 존재"],
          datasets: [
            {
              data: [data.근원부서_존재하지않음, data.근원부서_존재], // 서버에서 받은 데이터
              backgroundColor: ["#FF6384", "#36A2EB"],
              hoverBackgroundColor: ["#FF6384", "#36A2EB"],
            },
          ],
        };

        // 파이 차트 생성
        new Chart(ctx, {
          type: "pie",
          data: chartData,
          options: {
            responsive: true,
            plugins: {
              title: {
                display: true,
                text: "P1 개정도 현황", // 그래프 상단의 제목
                font: {
                  size: 18,
                },
              },
              legend: {
                position: "bottom", // 범례(legend) 위치를 하단에 표시
              },
              datalabels: {
                color: "white", // 데이터 라벨의 색상
                formatter: (value, ctx) => {
                  let label = ctx.chart.data.labels[ctx.dataIndex];
                  return label + ": " + value; // 레이블과 값 함께 표시
                },
                font: {
                  weight: "bold",
                },
              },
            },
          },
          plugins: [ChartDataLabels], // 데이터 라벨 플러그인 적용
        });
      } catch (error) {
        console.error("데이터를 가져오는 중 오류 발생: ", error);
      }
    };

    fetchDataAndDrawChart(); // 데이터 가져와서 차트 그리기
  }, []);

  return (
    <div className="notice">
      <h2>공정현황 그래프</h2>
      <canvas
        ref={chartRef}
        style={{ width: "50%", height: "50%" }}
      ></canvas>{" "}
      {/* 차트가 그려질 캔버스 */}
    </div>
  );
};

export default Notice;
