import React, { useEffect, useState } from "react";
import { Pie, Bar } from "react-chartjs-2";
import {
  Chart as ChartJS,
  Tooltip,
  Legend,
  Title,
  ArcElement,
  BarElement,
  CategoryScale,
  LinearScale,
} from "chart.js";

// Chart.js 구성 요소 등록
ChartJS.register(
  Tooltip,
  Legend,
  Title,
  ArcElement,
  BarElement,
  CategoryScale,
  LinearScale
);

function Graph_5() {
  const [mainData, setMainData] = useState(null);
  const [detailData, setDetailData] = useState(null);
  const [selectedReason, setSelectedReason] = useState(null);

  useEffect(() => {
    // 서버에서 데이터 가져오기
    fetch("/status-data2")
      .then((response) => response.json())
      .then((data) => setMainData(data))
      .catch((error) => console.error("Error fetching data:", error));
  }, []);

  const handlePieClick = (event, chartElement) => {
    if (chartElement.length > 0) {
      const index = chartElement[0].index;
      const label = mainData.labels[index];

      if (label === "불량") {
        fetch("/fault-details")
          .then((response) => response.json())
          .then((data) => {
            setDetailData(data);
            setSelectedReason(label); // 선택된 사유코드 설명
          })
          .catch((error) =>
            console.error("Error fetching fault details:", error)
          );
      }
    }
  };

  const handleDetailPieClick = (event, chartElement) => {
    if (chartElement.length > 0) {
      const index = chartElement[0].index;
      const reasonCode = detailData.labels[index];

      fetch(`/detail-data/${reasonCode}`)
        .then((response) => response.json())
        .then((data) => setDetailChartData(data))
        .catch((error) => console.error("Error fetching detail data:", error));
    }
  };

  const [detailChartData, setDetailChartData] = useState(null);

  return (
    <div>
      <h1>사유코드 설명 파이 차트</h1>
      {mainData && (
        <Pie
          data={{
            labels: ["정상", "불량"],
            datasets: [
              {
                data: [mainData.정상, mainData.불량],
                backgroundColor: ["#36A2EB", "#FF6384"],
                hoverBackgroundColor: ["#36A2EB", "#FF6384"],
              },
            ],
          }}
          options={{
            responsive: false,
            onClick: handlePieClick,
          }}
        />
      )}

      {detailData && selectedReason === "불량" && (
        <Pie
          data={{
            labels: detailData.map((item) => item["사유코드설명"]),
            datasets: [
              {
                data: detailData.map((item) => item.count),
                backgroundColor: [
                  "#FF9F40",
                  "#FF6384",
                  "#36A2EB",
                  "#4BC0C0",
                  "#4BC0FF",
                ],
              },
            ],
          }}
          options={{
            responsive: false,
            onClick: handleDetailPieClick,
          }}
        />
      )}

      {detailChartData && (
        <Bar
          data={{
            labels: detailChartData.map((item) => item["선종"]),
            datasets: [
              {
                data: detailChartData.map((item) => item.count),
                backgroundColor: ["#FF6384", "#36A2EB", "#FFCE56"],
              },
            ],
          }}
          options={{
            responsive: false,
          }}
        />
      )}
    </div>
  );
}

export default Graph_5;
