import React from 'react';

const NewsList = ({ newsData }) => {
    if (newsData.length === 0) {
        return <p>Loading news...</p>;
    }

    return (
        <div>
            {newsData.map((news) => (
                <div key={news.id} className="news-item">
                    <h2>{news.title}</h2>
                    <p>{news.description}</p>
                    <p><a href={news.link} target="_blank" rel="noopener noreferrer">Read more</a></p>
                    <p>Published on: {new Date(news.pubDate).toLocaleString()}</p>  {/* pubDate를 읽기 쉽게 표시 */}
                </div>
            ))}
        </div>
    );
};

export default NewsList;
