import React, { useState, useEffect } from 'react';
import './App.css';
import News from './News';  // News 컴포넌트 임포트

const App = () => {
  const [newsData, setNewsData] = useState([]);

  useEffect(() => {
    // 백엔드에서 데이터를 가져오는 함수
    const fetchNews = async () => {
      try {
        const response = await fetch('/api/news');
        const data = await response.json();
        setNewsData(data);  // 뉴스 데이터를 상태로 설정
      } catch (error) {
        console.error('Error fetching news data:', error);
      }
    };

    fetchNews();
  }, []);

  return (
    <div className="App">
      <header className="App-header">
        <h1>Latest News</h1>
        <News newsData={newsData} />  {/* News 컴포넌트에 데이터 전달 */}
      </header>
    </div>
  );
};

export default App;
